import React, { useState, useEffect } from 'react';
import { View, Text, Button, ScrollView, StyleSheet  } from 'react-native';
import { initializeApp } from '@firebase/app';
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, onAuthStateChanged, signOut } from '@firebase/auth';
import LoginForm from './LoginForm';
import SignUpForm from './SignUpForm';
import HomeScreen from './HomeScreen';
import ForgotPassword from './ForgotPassword';



const firebaseConfig = {
  apiKey: "AIzaSyDGfl7xaCepBqjcWEl0KM_EpJ4UCkw0r-Y",
  authDomain: "fir-react-a8bde.firebaseapp.com",
  projectId: "fir-react-a8bde",
  storageBucket: "fir-react-a8bde.appspot.com",
  messagingSenderId: "648258568034",
  appId: "1:648258568034:web:51d757ce7b6d50f5b0763b",
  measurementId: "G-Z53QJ36W5G"
};

const app = initializeApp(firebaseConfig);

const AuthScreen = ({ isLogin }) => {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text style={{ fontSize: 20, fontWeight: 'bold', marginBottom: 20 }}>Get Started with</Text>
      <Text style={{ fontSize: 30, fontWeight: 'bold', marginBottom: 20, color: '#3498db' }}>FinEase</Text>
      {isLogin ? <LoginForm /> : <SignUpForm /> }
    </View>
  );
};

const App = () => {
  const [user, setUser] = useState(null);
  const [isLogin, setIsLogin] = useState(true);

  const auth = getAuth(app);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
    });
    return () => unsubscribe();
  }, [auth]);

  const handleAuthentication = async (email, password) => {
    try {
      if (email === '' && password === '') {
        // If both email and password are empty, it means logout
        console.log('User logged out successfully!');
        await signOut(auth);
        return; // Exit the function after logout
      }
      if (!email || !password) {
        throw new Error('Please enter both email and password.');
      }
  
      if (user) {
        console.log('User logged out successfully!');
        await signOut(auth);
      } else {
        if (isLogin) {
          await signInWithEmailAndPassword(auth, email, password);
          console.log('User signed in successfully!');
        } else {
          await createUserWithEmailAndPassword(auth, email, password);
          console.log('User created successfully!');
        }
      }
    } catch (error) {
      console.error('Authentication error:', error);
    }
  };

  return (
    <ScrollView contentContainerStyle={{ flexGrow: 1, alignItems: 'center', justifyContent: 'center' }}>
      {user ? (
        <HomeScreen user={user} handleAuthentication={handleAuthentication} />
      ) : (
        <AuthScreen isLogin={isLogin} />
      )}
    </ScrollView>
  );
};

export default App;
